CREATE TABLE students ( email VARCHAR(225) PRIMARY KEY, name VARCHAR(255), rollno INTEGER, place VARCHAR(255), password VARCHAR(255) );
CREATE TABLE books ( bookId INTEGER PRIMARY KEY AUTO_INCREMENT, bookName VARCHAR(255), author VARCHAR(255), price INTEGER, quantity INTEGER );
CREATE TABLE myBooks ( saleId INTEGER PRIMARY KEY AUTO_INCREMENT, bookId INTEGER, student VARCHAR(255), FOREIGN KEY (student) REFERENCES students(email), FOREIGN KEY (bookId) REFERENCES books(bookId) );
CREATE TABLE marks ( markId INTEGER PRIMARY KEY AUTO_INCREMENT, student VARCHAR(255), subject VARCHAR(255), marks INTEGER, maximum INTEGER, sem INTEGER,  FOREIGN KEY (student) REFERENCES students(email));
CREATE TABLE parents ( parId INTEGER PRIMARY KEY AUTO_INCREMENT, student VARCHAR(255), name VARCHAR(255), age INTEGER, job VARCHAR(255), relation VARCHAR(255), FOREIGN KEY (student) REFERENCES students(email));